$(document).ready(function(){if(!Modernizr.touch){var e=9,t=14,n=20;$("#resetFont").click(function(){var e=document.getElementsByTagName("p");for(i=0;i<e.length;i++)e[i].style.fontSize=t+"px"}),$("#increaseFont").click(function(){var e=document.getElementsByTagName("p");for(i=0;i<e.length;i++){if(e[i].style.fontSize)var r=parseInt(e[i].style.fontSize.replace("px",""));else var r=t;r!=n&&(r+=1),e[i].style.fontSize=r+"px"}}),$("#decreaseFont").click(function(){var n=document.getElementsByTagName("p");for(i=0;i<n.length;i++){if(n[i].style.fontSize)var r=parseInt(n[i].style.fontSize.replace("px",""));else var r=t;r!=e&&(r-=1),n[i].style.fontSize=r+"px"}}),$("#font_control").show()}});