$(document).ready(function(){if(!Modernizr.touch){var a=9,b=14,c=20;$("#resetFont").click(function(){var a=document.getElementsByTagName("p");for(i=0;i<a.length;i++)a[i].style.fontSize=b+"px"}),$("#increaseFont").click(function(){var a=document.getElementsByTagName("p");for(i=0;i<a.length;i++){if(a[i].style.fontSize)var d=parseInt(a[i].style.fontSize.replace("px",""));else var d=b;d!=c&&(d+=1),a[i].style.fontSize=d+"px"}}),$("#decreaseFont").click(function(){var c=document.getElementsByTagName("p");for(i=0;i<c.length;i++){if(c[i].style.fontSize)var d=parseInt(c[i].style.fontSize.replace("px",""));else var d=b;d!=a&&(d-=1),c[i].style.fontSize=d+"px"}}),$("#font_control").show()}});